     Class   Luas_Persegi { 

         //memiliki 2 method yaitu Volume dan Luas 
         function   Luas ( $panjang ,   $lebar ){ 
             echo   Luas dengan Prosedur =    .   ( $panjang   *   $lebar ); 
         } 

