             $volume   =   $panjang * $lebar * $tinggi ; 
             return   $volume ;   //respon nilai 
         } 
     } 

     //objek dari persegi panjang 
     $persegi   =   new   Luas_Persegi (); 

     //implementasi Prosedur 
     $persegi - Luas ( 10 , 5 ); 
